import { ADD_TODO, ADD_TODO_ERROR, GET_TODO } from "../actions/types";
import { createStore } from "redux";

const initialState = {
    todo: null,
    todos: []
  },
  todoReducer = createStore(function(state = initialState, action) {
    const { type, payload } = action;
    switch (type) {
      case ADD_TODO:
        return {
          todo: payload,
          todos: [],
          ...state
        };
      case ADD_TODO_ERROR:
        return {
          todo: null,
          todos: [],
          ...state
        };
      case GET_TODO:
        return {
          todo: payload,
          todos: [],
          ...state
        };
      default:
        return state;
    }
  });

export default todoReducer;
