import { ADD_TODO, ADD_TODO_ERROR } from "../actions/types";
import { createStore } from "redux";

const initialState = {
    todo: null,
    todos: []
  },
  todoReducer = createStore(function(state = initialState, action) {
    const { type, payload } = action;
    switch (type) {
      case ADD_TODO:
        return {
          todoName: payload,
          ...state
        };
      case ADD_TODO_ERROR:
        return {
          todoName: null,
          ...state
        };
      default:
        return state;
    }
  });

export default todoReducer;
