import { Component, Fragment } from "inferno";
import { connect } from "inferno-redux";
import { addTodo, getTodos } from "./actions/todoActions";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      todos: []
    };
  }

  componentDidMount() {
    this.props.getTodos();
  }

  render() {
    return (
      <div className="App">
        <h1>Todo List</h1>
        {getTodos.map(todo => (
          <Fragment>
            <ul key={todo.id}>
              <li>{todo.todoName}</li>
            </ul>
          </Fragment>
        ))}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  todo: state.todo
});

export default connect(
  mapStateToProps,
  { addTodo, getTodos }
)(App);
