import { connect } from "inferno-redux";
import { editTodo, getTodos, deleteTodo } from "../actions/todoActions";
import { Button } from "inferno-bootstrap";

const IndividualTodo = ({ todos, deleteTodo }) => (
  <div>
    <ul>
      <li> {todos.todoName} </li>
      <Button color="primary" onClick={() => deleteTodo(todos._id)}>
        Delete
      </Button>
      color="primary"
    </ul>
  </div>
);

const mapStateToProps = state => ({
  todo: state.todo
});

export default connect(
  mapStateToProps,
  { editTodo, getTodos, deleteTodo }
)(IndividualTodo);
