import {
  ADD_TODO,
  ADD_TODO_ERROR,
  GET_TODO,
  GET_TODO_ERROR,
  GET_TODOS
} from "../actions/types";
import { createStore } from "redux";

const initialState = {
  todo: null,
  todos: []
};

export default function(state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case ADD_TODO:
      return {
        todo: payload,
        todos: [],
        ...state
      };
    case ADD_TODO_ERROR:
    case GET_TODO_ERROR:
      return {
        todo: null,
        todos: [],
        ...state
      };
    case GET_TODO:
      return {
        todo: payload,
        todos: [],
        ...state
      };
    case GET_TODOS:
      return {
        todo: null,
        todos: payload,
        ...state
      };
    default:
      return state;
  }
}
