};

const IndividualTodo = ({ todos }) => (
  <div>
    <ul>
      <li> {todos.todoName} </li>
      <button onClick={handleEditTodo}>Edit</button>
      <button onClick={handleDeleteTodo}>Delete</button>
    </ul>
  </div>
);

const mapStateToProps = state => ({
  todo: state.todo
});

export default connect(
  mapStateToProps,
  { addTodo, getTodo, editTodo, getTodos, deleteTodo }
)(IndividualTodo);
