import { Component, Fragment } from "inferno";
import { connect } from "inferno-redux";
import { addTodo, getTodos } from "../actions/todoActions";

class Todo extends Component {
  constructor() {
    super();

    this.state = {
      todoName: ""
    };
  }

  componentDidMount() {
    this.props.getTodos();
  }

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleAddTodo = e => {
    e.preventDefault();
    const { todoName } = this.state;
    const { addTodo, getTodos } = this.props;

    let newTodo = {
      todoName
    };

    this.setState({ todoName: "" });
    addTodo(newTodo);
    getTodos();
  };

  render() {
    const { todos } = this.props.todo;
    const { todoName } = this.state;

    return (
      <div>
        <h1>Todo</h1>
        <Fragment>
          <input
            type="text"
            name="todoName"
            value={todoName}
            onInput={this.handleChange}
          />
          <button onClick={this.handleAddTodo} type="submit">
            Add
          </button>
        </Fragment>
        <Fragment>
          {todos.map(todo => (
            <ul key={todo.id}>
              <li>{todo.todoName}</li>
            </ul>
          ))}
        </Fragment>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  todo: state.todo
});

export default connect(
  mapStateToProps,
  { getTodos, addTodo }
)(Todo);
